css:
.pricing-checklist-section { padding: 5rem 0; font-family: 'Inter', sans-serif; }
.pricing-checklist-section .container { max-width: 1000px; margin: 0 auto; padding: 0 1rem; }
.pricing-checklist-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-checklist-section .pricing-table { border: 1px solid #e5e7eb; border-radius: 1rem; overflow: hidden; }
.pricing-checklist-section .plan-headers, .pricing-checklist-section .feature-row, .pricing-checklist-section .cta-row { display: grid; grid-template-columns: 2fr 1fr 1fr 1fr; text-align: center; }
.pricing-checklist-section .plan-headers { background: #f9fafb; }
.pricing-checklist-section .plan-col, .pricing-checklist-section .feature-col, .pricing-checklist-section .feature-row > div, .pricing-checklist-section .cta-row > div { padding: 1rem; }
.pricing-checklist-section .plan-col h3 { font-size: 1.2rem; font-weight: 600; }
.pricing-checklist-section .plan-col p { font-size: 1.5rem; font-weight: 700; }
.pricing-checklist-section .feature-row { border-top: 1px solid #e5e7eb; }
.pricing-checklist-section .feature-name { text-align: left; font-weight: 500; }
.pricing-checklist-section .cta-row { border-top: 1px solid #e5e7eb; padding: 1.5rem 0; }
.pricing-checklist-section .popular { background: #eff6ff; }
.pricing-checklist-section .btn { padding: 0.6rem 1.2rem; border-radius: 6px; text-decoration: none; border: 1px solid #ccc; color: #333; }
.pricing-checklist-section .btn-primary { background: #3b82f6; color: #fff; border: none; }
