css:
.pricing-neon-section { padding: 5rem 0; background-color: #000; color: #fff; font-family: 'Inter', sans-serif; }
.pricing-neon-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-neon-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; text-shadow: 0 0 10px #0ea5e9; }
.pricing-neon-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2.5rem; }
.pricing-neon-section .pricing-card { background-color: #111; border-radius: 1rem; padding: 2.5rem; transition: transform 0.3s; }
.pricing-neon-section .pricing-card:hover { transform: translateY(-10px); }
.pricing-neon-section .plan-1 { border: 2px solid #0ea5e9; box-shadow: 0 0 20px rgba(14, 165, 233, 0.5); }
.pricing-neon-section .plan-2 { border: 2px solid #ec4899; box-shadow: 0 0 25px rgba(236, 72, 153, 0.6); }
.pricing-neon-section .plan-3 { border: 2px solid #10b981; box-shadow: 0 0 20px rgba(16, 185, 129, 0.5); }
.pricing-neon-section .icon { font-size: 2.5rem; margin-bottom: 1rem; }
.pricing-neon-section .plan-1 .icon { color: #0ea5e9; }
.pricing-neon-section .plan-2 .icon { color: #ec4899; }
.pricing-neon-section .plan-3 .icon { color: #10b981; }
.pricing-neon-section h3 { font-size: 1.5rem; margin-bottom: 1rem; }
.pricing-neon-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-neon-section ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-neon-section li { margin-bottom: 0.5rem; }
.pricing-neon-section .btn { display: inline-block; padding: 0.8rem 2rem; border-radius: 8px; text-decoration: none; font-weight: 600; }
.pricing-neon-section .plan-1 .btn { border: 1px solid #0ea5e9; color: #0ea5e9; }
.pricing-neon-section .plan-2 .btn { background-color: #ec4899; color: #fff; }
.pricing-neon-section .plan-3 .btn { border: 1px solid #10b981; color: #10b981; }
