css:
.pricing-lines-section { padding: 5rem 0; font-family: 'Inter', sans-serif; }
.pricing-lines-section .container { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; max-width: 1200px; margin: 0 auto; }
.pricing-lines-section .pricing-card { display: flex; flex-direction: column; border: 1px solid #e5e7eb; border-radius: 4px; }
.pricing-lines-section .header { padding: 2rem; text-align: center; border-bottom: 1px solid #e5e7eb; }
.pricing-lines-section h3 { font-size: 1.2rem; font-weight: 500; text-transform: uppercase; letter-spacing: 1px; margin-bottom: 0.5rem; }
.pricing-lines-section .price { font-size: 2.5rem; font-weight: 700; }
.pricing-lines-section .features { padding: 2rem; flex-grow: 1; }
.pricing-lines-section ul { list-style: none; padding: 0; }
.pricing-lines-section li { margin-bottom: 1rem; text-align: center; color: #4b5563; }
.pricing-lines-section .footer { padding: 2rem; border-top: 1px solid #e5e7eb; text-align: center; }
.pricing-lines-section .btn { display: inline-block; padding: 0.8rem 2rem; border: 1px solid #111; text-decoration: none; color: #111; }
.pricing-lines-section .btn-primary { background-color: #111; color: #fff; }
.pricing-lines-section .popular { border-width: 2px; border-color: #111; }
