css:
.pricing-enterprise-section { padding: 5rem 0; background-color: #f1f5f9; }
.pricing-enterprise-section .container { max-width: 1000px; margin: 0 auto; text-align: center; }
.pricing-enterprise-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-enterprise-section .pricing-grid { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media(min-width: 768px) { .pricing-enterprise-section .pricing-grid { grid-template-columns: 1fr 1.5fr 1fr; } }
.pricing-enterprise-section .pricing-card { background: #fff; border-radius: 1rem; padding: 2rem; box-shadow: 0 5px 20px rgba(0,0,0,0.05); }
.pricing-enterprise-section h3 { font-size: 1.25rem; font-weight: 600; margin-bottom: 1rem; }
.pricing-enterprise-section .price { font-size: 2rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-enterprise-section .large-card { padding: 3rem; }
.pricing-enterprise-section .large-card h3 { font-size: 1.8rem; }
.pricing-enterprise-section .large-card p { color: #64748b; margin-bottom: 1.5rem; }
.pricing-enterprise-section .large-card ul { list-style: none; padding: 0; margin-bottom: 2rem; text-align: left; }
.pricing-enterprise-section .large-card li { margin-bottom: 0.5rem; }
.pricing-enterprise-section .btn { display: block; padding: 0.8rem; border: 1px solid #cbd5e1; border-radius: 8px; text-decoration: none; color: #334155; }
.pricing-enterprise-section .btn-primary { background: #3b82f6; color: #fff; border: none; padding: 1rem; }
