css:
.pricing-feature-grid-section { padding: 5rem 0; background-color: #f9fafb; }
.pricing-feature-grid-section .container { max-width: 1000px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-feature-grid-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-feature-grid-section .pricing-table { background: #fff; border-radius: 1rem; box-shadow: 0 10px 30px rgba(0,0,0,0.05); overflow: hidden; text-align: center; }
.pricing-feature-grid-section .header-row, .pricing-feature-grid-section .feature-row, .pricing-feature-grid-section .footer-row { display: grid; grid-template-columns: 2fr 1fr 1fr 1fr; border-bottom: 1px solid #e5e7eb; }
.pricing-feature-grid-section .header-row > div, .pricing-feature-grid-section .feature-row > div, .pricing-feature-grid-section .footer-row > div { padding: 1.5rem 1rem; }
.pricing-feature-grid-section .feature-header { text-align: left; font-weight: 600; }
.pricing-feature-grid-section .plan-header h3 { font-size: 1.2rem; font-weight: 600; }
.pricing-feature-grid-section .plan-header p { font-size: 1.5rem; font-weight: 700; }
.pricing-feature-grid-section .feature-row > div:first-child { text-align: left; font-weight: 500; }
.pricing-feature-grid-section .popular { background-color: #eff6ff; }
.pricing-feature-grid-section .footer-row { border-bottom: none; }
.pricing-feature-grid-section .btn { padding: 0.5rem 1rem; border: 1px solid #ccc; border-radius: 6px; text-decoration: none; color: #333; }
.pricing-feature-grid-section .btn-primary { background-color: #3b82f6; color: #fff; border: none; }
