css:
.pricing-rows-section { padding: 5rem 0; }
.pricing-rows-section .container { max-width: 1000px; margin: 0 auto; }
.pricing-rows-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-rows-section .plan-headers, .pricing-rows-section .feature-row { display: grid; grid-template-columns: 1.5fr 1fr 1fr 1fr; gap: 1rem; align-items: center; padding: 1.5rem 0; border-bottom: 1px solid #e5e7eb; }
.pricing-rows-section .plan-headers { border-bottom-width: 2px; }
.pricing-rows-section .plan-headers h3 { font-size: 1.2rem; text-align: center; }
.pricing-rows-section .feature-row h4 { font-size: 1.1rem; font-weight: 600; }
.pricing-rows-section .feature-row p { text-align: center; color: #6b7280; }
@media (max-width: 640px) {
    .pricing-rows-section .plan-headers, .pricing-rows-section .feature-row { grid-template-columns: 1fr; text-align: left; } 
    .pricing-rows-section .feature-row p { text-align: left; }
}
