css:
.pricing-drawn-section { padding: 5rem 0; background-color: #fdfaf3; font-family: 'Patrick Hand', cursive; }
.pricing-drawn-section .container { max-width: 1100px; margin: 0 auto; text-align: center; }
.pricing-drawn-section .section-title { font-size: 3rem; margin-bottom: 3rem; }
.pricing-drawn-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2.5rem; }
.pricing-drawn-section .pricing-card { border: 2px solid #333; border-radius: 10px 255px 15px 225px / 225px 15px 255px 15px; padding: 2.5rem; transition: transform 0.3s; background: #fff; }
.pricing-drawn-section .pricing-card:hover { transform: rotate(1deg); }
.pricing-drawn-section .popular { transform: scale(1.05) rotate(-1deg); border-color: #3b82f6; }
.pricing-drawn-section h3 { font-size: 2rem; }
.pricing-drawn-section .price { font-size: 3.5rem; font-weight: 700; margin: 1rem 0; }
.pricing-drawn-section ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-drawn-section li { margin-bottom: 0.5rem; font-size: 1.2rem; }
.pricing-drawn-section .btn { display: inline-block; padding: 0.8rem 2rem; border: 2px solid #333; border-radius: 255px 15px 225px 15px / 15px 225px 15px 255px; text-decoration: none; color: #333; font-size: 1.2rem; }
.pricing-drawn-section .btn-primary { background: #3b82f6; color: #fff; border-color: #3b82f6; }
