css:
.pricing-industrial-section { padding: 5rem 0; background: #222; color: #eee; font-family: 'Oswald', sans-serif; text-transform: uppercase; }
.pricing-industrial-section .container { max-width: 1100px; margin: 0 auto; }
.pricing-industrial-section .section-title { text-align: center; font-size: 3rem; letter-spacing: 2px; margin-bottom: 3rem; }
.pricing-industrial-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 1rem; }
.pricing-industrial-section .pricing-card { background: linear-gradient(145deg, #333, #111); padding: 2rem; text-align: center; border-top: 4px solid #444; }
.pricing-industrial-section .popular { border-top-color: #f59e0b; }
.pricing-industrial-section h3 { font-size: 1.5rem; letter-spacing: 1px; margin-bottom: 1rem; }
.pricing-industrial-section .price { font-size: 3.5rem; font-weight: 700; margin-bottom: 2rem; }
.pricing-industrial-section ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-industrial-section li { margin-bottom: 0.75rem; }
.pricing-industrial-section .btn { display: inline-block; padding: 1rem 2rem; background: transparent; border: 2px solid #555; color: #ccc; text-decoration: none; font-weight: 700; }
.pricing-industrial-section .btn-primary { border-color: #f59e0b; color: #f59e0b; }
