css:
.pricing-minimalist-section { padding: 5rem 0; background-color: #fff; font-family: 'Inter', sans-serif; }
.pricing-minimalist-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-minimalist-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; color: #111; }
.pricing-minimalist-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-minimalist-section .pricing-card { border: 1px solid #e5e7eb; border-radius: 1rem; padding: 2.5rem; text-align: left; }
.pricing-minimalist-section h3 { font-size: 1.5rem; font-weight: 600; margin-bottom: 0.5rem; }
.pricing-minimalist-section .description { color: #6b7280; font-size: 0.9rem; margin-bottom: 1.5rem; min-height: 40px; }
.pricing-minimalist-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; border-bottom: 1px solid #e5e7eb; padding-bottom: 1.5rem; }
.pricing-minimalist-section .price span { font-size: 1rem; color: #6b7280; font-weight: 400; }
.pricing-minimalist-section .btn { display: block; width: 100%; text-align: center; padding: 1rem; border: 1px solid #111; color: #111; text-decoration: none; border-radius: 8px; font-weight: 500; margin-bottom: 1.5rem; }
.pricing-minimalist-section .btn-primary { background-color: #3b82f6; color: #fff; border-color: #3b82f6; }
.pricing-minimalist-section ul { list-style: none; padding: 0; }
.pricing-minimalist-section li { display: flex; gap: 0.5rem; align-items: center; margin-bottom: 0.5rem; color: #4b5563; }
.pricing-minimalist-section .popular { border-color: #3b82f6; border-width: 2px; }
