css:
.pricing-organic-section { padding: 5rem 0; background-color: #fefce8; font-family: 'Inter', sans-serif; }
.pricing-organic-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-organic-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; color: #78350f; }
.pricing-organic-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-organic-section .pricing-card { background: #fff; border-radius: 1.5rem; padding: 2.5rem; position: relative; overflow: hidden; box-shadow: 0 10px 25px rgba(0,0,0,0.05); }
.pricing-organic-section .pricing-card::before { content: ''; position: absolute; top: -50%; left: -50%; width: 200%; height: 200%; border-radius: 45% 55% 60% 40% / 40% 50% 50% 60%; transform: rotate(45deg); opacity: 0.1; z-index: 0; transition: all 0.5s ease; }
.pricing-organic-section .pricing-card:nth-child(1)::before { background-color: #a7f3d0; }
.pricing-organic-section .pricing-card:nth-child(2)::before { background-color: #bfdbfe; }
.pricing-organic-section .pricing-card:nth-child(3)::before { background-color: #fbcfe8; }
.pricing-organic-section .pricing-card > * { position: relative; z-index: 1; }
.pricing-organic-section .pricing-card:hover::before { transform: rotate(90deg) scale(1.2); }
.pricing-organic-section h3 { font-size: 1.5rem; font-weight: 600; margin-bottom: 1rem; }
.pricing-organic-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-organic-section .price span { font-size: 1rem; color: #6b7280; }
.pricing-organic-section ul { list-style: none; padding: 0; margin-bottom: 2rem; color: #4b5563; }
.pricing-organic-section li { margin-bottom: 0.5rem; }
.pricing-organic-section .btn { display: inline-block; padding: 0.8rem 2rem; border-radius: 99px; text-decoration: none; font-weight: 500; border: 1px solid; }
.pricing-organic-section .pricing-card:nth-child(1) .btn { border-color: #34d399; color: #059669; }
.pricing-organic-section .pricing-card:nth-child(2) .btn { border-color: #93c5fd; color: #3b82f6; }
.pricing-organic-section .pricing-card:nth-child(3) .btn { border-color: #f9a8d4; color: #ec4899; }
