css:
.pricing-pastel-section { padding: 5rem 0; background: #fdf2f8; font-family: 'Inter', sans-serif; }
.pricing-pastel-section .container { max-width: 1100px; margin: 0 auto; text-align: center; }
.pricing-pastel-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; color: #9d174d; }
.pricing-pastel-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-pastel-section .pricing-card { background: #fff; border-radius: 1.5rem; padding: 2.5rem; box-shadow: 0 10px 30px rgba(0,0,0,0.05); }
.pricing-pastel-section h3 { font-size: 1.5rem; margin-bottom: 1rem; }
.pricing-pastel-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-pastel-section .plan-1 { color: #06b6d4; } .pricing-pastel-section .plan-1 .btn { background: #06b6d4; }
.pricing-pastel-section .plan-2 { color: #ec4899; } .pricing-pastel-section .plan-2 .btn-primary { background: #ec4899; }
.pricing-pastel-section .plan-3 { color: #f59e0b; } .pricing-pastel-section .plan-3 .btn { background: #f59e0b; }
.pricing-pastel-section ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-pastel-section li { margin-bottom: 0.5rem; }
.pricing-pastel-section .btn { display: inline-block; padding: 0.8rem 2rem; color: #fff; border-radius: 99px; text-decoration: none; }
