css:
.pricing-shadow-pop-section { padding: 5rem 0; background-color: #fff; font-family: 'Inter', sans-serif; }
.pricing-shadow-pop-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-shadow-pop-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-shadow-pop-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-shadow-pop-section .pricing-card { border: 1px solid #e5e7eb; border-radius: 1rem; padding: 2.5rem; transition: transform 0.3s ease, box-shadow 0.3s ease; }
.pricing-shadow-pop-section .pricing-card:hover { transform: translateY(-10px); box-shadow: 0 20px 40px rgba(59, 130, 246, 0.2); }
.pricing-shadow-pop-section .icon { font-size: 2.5rem; margin-bottom: 1rem; color: var(--color-primary); }
.pricing-shadow-pop-section h3 { font-size: 1.5rem; font-weight: 600; margin-bottom: 1rem; }
.pricing-shadow-pop-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-shadow-pop-section ul { list-style: none; padding: 0; margin-bottom: 2rem; color: #6b7280; }
.pricing-shadow-pop-section li { margin-bottom: 0.5rem; }
.pricing-shadow-pop-section .btn { display: inline-block; padding: 0.8rem 2rem; border: 1px solid #111; color: #111; text-decoration: none; border-radius: 8px; font-weight: 500; }
.pricing-shadow-pop-section .btn-primary { background-color: #3b82f6; color: #fff; border: none; }
.pricing-shadow-pop-section .popular { box-shadow: 0 15px 30px rgba(59, 130, 246, 0.15); }
