css:
.pricing-slider-section { padding: 5rem 0; }
.pricing-slider-section .container { max-width: 900px; margin: 0 auto; text-align: center; }
.pricing-slider-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-slider-section .slider-ui { margin-bottom: 3rem; }
.pricing-slider-section .slider-track { height: 8px; background: #e5e7eb; border-radius: 4px; position: relative; }
.pricing-slider-section .slider-handle { width: 24px; height: 24px; border-radius: 50%; background: #3b82f6; position: absolute; top: 50%; transform: translate(-50%, -50%); cursor: grab; }
.pricing-slider-section .slider-labels { display: flex; justify-content: space-between; font-size: 0.9rem; color: #6b7280; margin-top: 0.75rem; }
.pricing-slider-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 2rem; }
.pricing-slider-section .pricing-card { background: #f9fafb; padding: 2rem; border-radius: 1rem; }
.pricing-slider-section h3 { font-size: 1.5rem; margin-bottom: 0.5rem; }
.pricing-slider-section .pricing-card p { color: #6b7280; }
