css:
.pricing-clay-section { padding: 5rem 0; background-color: #F0EBE3; font-family: 'Inter', sans-serif; }
.pricing-clay-section .container { max-width: 1100px; margin: 0 auto; text-align: center; }
.pricing-clay-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; color: #5C5470; }
.pricing-clay-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-clay-section .pricing-card { background-color: #F0EBE3; border-radius: 20px; padding: 2rem; box-shadow: 8px 8px 16px #D1CCC1, -8px -8px 16px #FFFFFF; transition: all 0.3s ease-in-out; }
.pricing-clay-section .pricing-card:hover { box-shadow: 4px 4px 8px #D1CCC1, -4px -4px 8px #FFFFFF; }
.pricing-clay-section .popular { box-shadow: inset 8px 8px 16px #D1CCC1, inset -8px -8px 16px #FFFFFF; }
.pricing-clay-section .popular:hover { box-shadow: inset 4px 4px 8px #D1CCC1, inset -4px -4px 8px #FFFFFF; }
.pricing-clay-section h3 { font-size: 1.5rem; color: #352F44; margin-bottom: 1rem; }
.pricing-clay-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; color: #352F44; }
.pricing-clay-section ul { list-style: none; padding: 0; margin-bottom: 2rem; color: #5C5470; }
.pricing-clay-section li { margin-bottom: 0.5rem; }
.pricing-clay-section .btn { display: inline-block; padding: 0.8rem 2rem; border-radius: 12px; text-decoration: none; font-weight: 600; color: #352F44; box-shadow: 4px 4px 8px #D1CCC1, -4px -4px 8px #FFFFFF; }
.pricing-clay-section .btn-primary { background: #B9B4C7; box-shadow: none; }
