css:
.pricing-split-tone-section { padding: 5rem 0; background-color: #fff; font-family: 'Inter', sans-serif; }
.pricing-split-tone-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-split-tone-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-split-tone-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-split-tone-section .pricing-card { border-radius: 1.5rem; overflow: hidden; box-shadow: 0 10px 30px rgba(0,0,0,0.1); display: flex; flex-direction: column; }
.pricing-split-tone-section .top-half { padding: 2rem; color: #fff; }
.pricing-split-tone-section .bottom-half { padding: 2rem; background-color: #f8fafc; flex-grow: 1; display: flex; flex-direction: column; }
.pricing-split-tone-section .plan-1 .top-half { background-color: #38bdf8; }
.pricing-split-tone-section .plan-2 .top-half { background-color: #a78bfa; }
.pricing-split-tone-section .plan-3 .top-half { background-color: #fbbf24; }
.pricing-split-tone-section .icon { font-size: 2.5rem; margin-bottom: 1rem; }
.pricing-split-tone-section h3 { font-size: 1.5rem; font-weight: 600; }
.pricing-split-tone-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-split-tone-section ul { list-style: none; padding: 0; margin-bottom: 2rem; flex-grow: 1; color: #475569; }
.pricing-split-tone-section li { margin-bottom: 0.5rem; }
.pricing-split-tone-section .btn { display: inline-block; padding: 0.8rem 2rem; border-radius: 8px; text-decoration: none; font-weight: 600; margin-top: auto; color: #fff; }
.pricing-split-tone-section .plan-1 .btn { background-color: #0ea5e9; }
.pricing-split-tone-section .plan-2 .btn { background-color: #8b5cf6; }
.pricing-split-tone-section .plan-3 .btn { background-color: #f59e0b; }
