css:
.pricing-vibrant-section { padding: 5rem 0; background-color: #f8f9fa; font-family: 'Inter', sans-serif; }
.pricing-vibrant-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-vibrant-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; color: var(--color-dark); }
.pricing-vibrant-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 2rem; align-items: center; }
.pricing-vibrant-section .pricing-card { border-radius: 1rem; padding: 2.5rem 2rem; color: #fff; transition: all 0.3s ease; text-align: center; }
.pricing-vibrant-section .pricing-card:hover { transform: scale(1.03); }
.pricing-vibrant-section .plan-1 { background-color: #3b82f6; }
.pricing-vibrant-section .plan-2 { background-color: #8b5cf6; }
.pricing-vibrant-section .plan-3 { background-color: #10b981; }
.pricing-vibrant-section .popular { transform: scale(1.1); }
.pricing-vibrant-section .icon { font-size: 2.5rem; margin-bottom: 1rem; }
.pricing-vibrant-section h3 { font-size: 1.5rem; font-weight: 600; margin-bottom: 1rem; }
.pricing-vibrant-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-vibrant-section ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-vibrant-section li { margin-bottom: 0.5rem; }
.pricing-vibrant-section .btn { display: inline-block; padding: 0.8rem 2rem; background-color: rgba(255,255,255,0.2); color: #fff; border-radius: 99px; text-decoration: none; font-weight: 500; }
.pricing-vibrant-section .btn-primary { background-color: #fff; color: #8b5cf6; }
