css:
.product-highlight-modern-minimal {
  font-family: 'Inter', sans-serif;
  background-color: #f8f9fa;
  padding: 80px 0;
}
.product-highlight-modern-minimal .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}
.product-highlight-modern-minimal .highlight-content {
  display: flex;
  align-items: center;
  gap: 50px;
  flex-wrap: wrap;
  background-color: #ffffff;
  border-radius: 15px;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.07);
  padding: 40px;
}
.product-highlight-modern-minimal .image-container {
  flex: 1;
  min-width: 300px;
  display: flex;
  justify-content: center;
  align-items: center;
}
.product-highlight-modern-minimal .image-container img {
  max-width: 100%;
  height: auto;
  display: block;
  border-radius: 10px;
}
.product-highlight-modern-minimal .text-container {
  flex: 1;
  min-width: 300px;
}
.product-highlight-modern-minimal .overline {
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  color: #888;
  display: block;
  margin-bottom: 15px;
}
.product-highlight-modern-minimal h2 {
  font-size: 36px;
  color: #333;
  margin-top: 0;
  margin-bottom: 15px;
  font-weight: 700;
}
.product-highlight-modern-minimal p {
  font-size: 17px;
  color: #555;
  line-height: 1.7;
  margin-bottom: 25px;
}
.product-highlight-modern-minimal .price-and-action {
  display: flex;
  align-items: center;
  gap: 20px;
}
.product-highlight-modern-minimal .price {
  font-size: 28px;
  font-weight: 700;
  color: #2c3e50;
}
.product-highlight-modern-minimal .cta-button {
  background-color: #2c3e50;
  color: white;
  padding: 12px 30px;
  text-decoration: none;
  border-radius: 30px;
  font-size: 16px;
  font-weight: 600;
  transition: background-color 0.3s ease;
}
.product-highlight-modern-minimal .cta-button:hover {
  background-color: #34495e;
}

@media (max-width: 768px) {
  .product-highlight-modern-minimal .highlight-content {
    flex-direction: column;
    text-align: center;
  }
  .product-highlight-modern-minimal .image-container {
    margin-bottom: 30px;
    flex-basis: 100%;
  }
  .product-highlight-modern-minimal .price-and-action {
    justify-content: center;
  }
  .product-highlight-modern-minimal h2 {
    font-size: 30px;
  }
  .product-highlight-modern-minimal p {
    font-size: 16px;
  }
}

js:
window.addEventListener('load', () => {
  const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    const url = img.src;
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(() => {
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
