.product-showcase-geometric-pattern {
  font-family: 'Arial', sans-serif;
  background-color: #f7f7f7;
  padding: 60px 0;
  background-image: linear-gradient(45deg, rgba(230, 230, 230, 0.5) 25%, transparent 25%),
                    linear-gradient(-45deg, rgba(230, 230, 230, 0.5) 25%, transparent 25%),
                    linear-gradient(45deg, transparent 75%, rgba(230, 230, 230, 0.5) 75%),
                    linear-gradient(-45deg, transparent 75%, rgba(230, 230, 230, 0.5) 75%);
  background-size: 40px 40px;
  background-position: 0 0, 0 20px, 20px -20px, -20px 0px;
}
.product-showcase-geometric-pattern .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
  background-color: #fff;
  border-radius: 10px;
  padding: 50px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.07);
}
.product-showcase-geometric-pattern .pattern-header {
  text-align: center;
  margin-bottom: 40px;
  color: #333;
}
.product-showcase-geometric-pattern h1 {
  font-size: 36px;
  font-weight: 700;
  margin-bottom: 10px;
}
.product-showcase-geometric-pattern p {
  font-size: 17px;
  color: #777;
  line-height: 1.6;
}
.product-showcase-geometric-pattern .product-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}
.product-showcase-geometric-pattern .product-card {
  background-color: #f9f9f9;
  border-radius: 8px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  text-align: center;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
}
.product-showcase-geometric-pattern .product-image {
  height: 280px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #eee;
}
.product-showcase-geometric-pattern .product-card img {
  max-width: 80%;
  max-height: 80%;
  object-fit: contain;
  display: block;
}
.product-showcase-geometric-pattern .product-details {
  padding: 20px;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.product-showcase-geometric-pattern .product-details h2 {
  font-size: 24px;
  color: #333;
  margin-top: 0;
  margin-bottom: 10px;
  font-weight: 700;
}
.product-showcase-geometric-pattern .product-details p {
  font-size: 14px;
  color: #777;
  margin-bottom: 15px;
}
.product-showcase-geometric-pattern .product-details span {
  font-size: 19px;
  font-weight: 700;
  color: #e67e22;
}

@media (max-width: 768px) {
  .product-showcase-geometric-pattern {
    padding: 40px 0;
  }
  .product-showcase-geometric-pattern .container {
    padding: 30px;
  }
  .product-showcase-geometric-pattern h1 {
    font-size: 30px;
  }
  .product-showcase-geometric-pattern p {
    font-size: 16px;
  }
  .product-showcase-geometric-pattern .product-grid {
    grid-template-columns: 1fr;
  }
}

js:
window.addEventListener('load', () => {
  const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    const url = img.src;
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(() => {
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
