css:
.advanced-hover-products {
  padding: 60px 20px;
  background-color: #ffffff;
  font-family: 'Lato', sans-serif;
}

.advanced-hover-products h2 {
  text-align: center;
  font-size: 2.6em;
  color: #2c3e50;
  margin-bottom: 50px;
  font-weight: 700;
}

.product-grid-adv {
  display: grid;
  grid-template-columns: 1fr;
  gap: 30px;
  max-width: 1100px;
  margin: 0 auto;
}

.product-card-adv {
  background-color: #fdfdfd;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
  transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  display: flex;
  flex-direction: column;
}

.card-image-wrapper {
  position: relative;
  width: 100%;
  height: 280px;
  overflow: hidden;
}

.card-image-wrapper img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.5s cubic-bezier(0.25, 0.8, 0.25, 1);
}

.product-card-adv:hover {
  transform: translateY(-10px);
  box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
}

.product-card-adv:hover .card-image-wrapper img {
  transform: scale(1.05);
}

.card-content {
  padding: 25px;
  text-align: center;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.card-content h4 {
  font-size: 1.5em;
  color: #34495e;
  margin-bottom: 8px;
  font-weight: 700;
}

.card-content .description {
  font-size: 0.9em;
  color: #7f8c8d;
  line-height: 1.5;
  margin-bottom: 15px;
}

.price-tag {
  font-size: 1.3em;
  font-weight: bold;
  color: #e74c3c;
  margin-bottom: 20px;
}

.view-details {
  background-color: #3498db;
  color: #ffffff;
  border: none;
  padding: 12px 25px;
  border-radius: 25px;
  font-size: 0.95em;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.3s ease;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.view-details:hover {
  background-color: #2980b9;
  transform: translateY(-2px);
}

@media (min-width: 768px) {
  .product-grid-adv {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .product-grid-adv {
    grid-template-columns: repeat(4, 1fr);
  }
}

js:
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    images.forEach(img => {
        fetch(img.src)
            .then(response => response.json())
            .then(data => {
                if (data.hits && data.hits.length > 0) {
                    img.src = data.hits[0].largeImageURL;
                } else {
                    img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
                }
            })
            .catch(error => {
                console.error('Error fetching Pixabay image:', error);
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            });
    });
});
