css:
.products-section {
  padding: 40px 20px;
  background-color: #f8f9fa;
}

.products-container {
  display: grid;
  grid-template-columns: repeat(1, 1fr);
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
}

.product-card {
  background-color: #ffffff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.product-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.product-image img {
  width: 100%;
  height: 250px;
  object-fit: cover;
  display: block;
}

.product-details {
  padding: 20px;
}

.product-details h3 {
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 1.4em;
  color: #333333;
  margin-bottom: 10px;
  font-weight: 700;
}

.product-details p {
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 0.95em;
  color: #666666;
  line-height: 1.5;
  margin-bottom: 15px;
}

.product-details .price {
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  font-size: 1.2em;
  font-weight: bold;
  color: #333333;
  text-align: left;
}

@media (min-width: 768px) {
  .products-container {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .products-container {
    grid-template-columns: repeat(4, 1fr);
  }
}

/* Advanced Hover Animation */
.product-card {
  position: relative;
  overflow: hidden;
}

.product-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 50%, rgba(255, 255, 255, 0.2) 100%);
  opacity: 0;
  transition: opacity 0.5s ease;
}

.product-card:hover::before {
  opacity: 1;
}

.product-image img {
  transition: transform 0.5s ease;
}

.product-card:hover .product-image img {
  transform: scale(1.05);
}

js:
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    images.forEach(img => {
        fetch(img.src)
            .then(response => response.json())
            .then(data => {
                if (data.hits && data.hits.length > 0) {
                    img.src = data.hits[0].largeImageURL;
                } else {
                    img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
                }
            })
            .catch(error => {
                console.error('Error fetching Pixabay image:', error);
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            });
    });
});
