css:
.modern-product-cards {
  padding: 60px 20px;
  background-color: #f8f9fa;
  font-family: 'Poppins', sans-serif;
}

.modern-product-cards h2 {
  text-align: center;
  font-size: 2.8em;
  color: #343a40;
  margin-bottom: 40px;
  font-weight: 700;
}

.product-card-wrapper {
  display: grid;
  grid-template-columns: 1fr;
  gap: 30px;
  max-width: 1100px;
  margin: 0 auto;
}

.product-card-modern {
  background-color: #ffffff;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
  transition: transform 0.4s cubic-bezier(0.25, 0.8, 0.25, 1), box-shadow 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  display: flex;
  flex-direction: column;
}

.img-container {
  width: 100%;
  height: 280px;
  overflow: hidden;
  position: relative;
}

.img-container img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.5s cubic-bezier(0.25, 0.8, 0.25, 1);
}

.product-card-modern:hover {
  transform: translateY(-12px);
  box-shadow: 0 18px 35px rgba(0, 0, 0, 0.15);
}

.product-card-modern:hover .img-container img {
  transform: scale(1.08);
}

.card-info {
  padding: 25px;
  text-align: center;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.card-info h4 {
  font-size: 1.5em;
  color: #343a40;
  margin-bottom: 10px;
  font-weight: 700;
}

.card-info p {
  font-size: 0.95em;
  color: #6c757d;
  line-height: 1.6;
  margin-bottom: 15px;
}

.card-info .price {
  font-size: 1.3em;
  font-weight: bold;
  color: #dc3545;
  margin-bottom: 20px;
}

.shop-link {
  display: inline-block;
  background-color: #007bff;
  color: #ffffff;
  padding: 12px 30px;
  border-radius: 30px;
  text-decoration: none;
  font-weight: 600;
  font-size: 0.95em;
  transition: background-color 0.3s ease, transform 0.3s ease;
}

.shop-link:hover {
  background-color: #0056b3;
  transform: translateY(-2px);
}

@media (min-width: 768px) {
  .product-card-wrapper {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .product-card-wrapper {
    grid-template-columns: repeat(4, 1fr);
  }
}

js:
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    images.forEach(img => {
        fetch(img.src)
            .then(response => response.json())
            .then(data => {
                if (data.hits && data.hits.length > 0) {
                    img.src = data.hits[0].largeImageURL;
                } else {
                    img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
                }
            })
            .catch(error => {
                console.error('Error fetching Pixabay image:', error);
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            });
    });
});
