css:
.feature-cards-products {
  padding: 60px 20px;
  background-color: #fdfdfd;
  font-family: 'Merriweather', serif;
}

.feature-cards-products h2 {
  text-align: center;
  font-size: 2.8em;
  color: #333333;
  margin-bottom: 40px;
  font-weight: 700;
}

.feature-card-container {
  display: grid;
  grid-template-columns: 1fr;
  gap: 30px;
  max-width: 1000px;
  margin: 0 auto;
}

.feature-card {
  background-color: #ffffff;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
  transition: transform 0.3s ease;
  display: flex;
  flex-direction: column;
}

.feature-image {
  width: 100%;
  height: 250px;
  overflow: hidden;
}

.feature-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.4s ease;
}

.feature-card:hover {
  transform: translateY(-6px);
}

.feature-card:hover .feature-image img {
  transform: scale(1.04);
}

.feature-content {
  padding: 30px;
  text-align: center;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.feature-content h3 {
  font-size: 1.6em;
  color: #333333;
  margin-bottom: 10px;
  font-weight: 700;
}

.feature-content p {
  font-size: 1em;
  color: #666666;
  line-height: 1.6;
}

@media (min-width: 768px) {
  .feature-card-container {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .feature-card-container {
    grid-template-columns: repeat(3, 1fr);
  }
}

js:
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    images.forEach(img => {
        fetch(img.src)
            .then(response => response.json())
            .then(data => {
                if (data.hits && data.hits.length > 0) {
                    img.src = data.hits[0].largeImageURL;
                } else {
                    img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
                }
            })
            .catch(error => {
                console.error('Error fetching Pixabay image:', error);
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            });
    });
});
