css:
.split-layout-products {
  padding: 60px 0;
  background-color: #f4f7f6;
  font-family: 'Montserrat', sans-serif;
}

.split-layout-products h2 {
  text-align: center;
  font-size: 2.8em;
  color: #2c3e50;
  margin-bottom: 50px;
  font-weight: 700;
}

.split-container {
  max-width: 1200px;
  margin: 0 auto;
}

.split-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 60px;
}

.split-image {
  flex: 1;
  width: 100%;
  height: 400px;
  overflow: hidden;
}

.split-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.4s ease;
}

.split-item:hover .split-image img {
  transform: scale(1.05);
}

.split-text {
  flex: 1;
  padding: 50px 40px;
  background-color: #ffffff;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.split-text h3 {
  font-size: 2em;
  color: #34495e;
  margin-bottom: 15px;
  font-weight: 700;
}

.split-text p {
  font-size: 1.05em;
  color: #7f8c8d;
  line-height: 1.7;
  margin-bottom: 30px;
}

.cta-button {
  display: inline-block;
  background-color: #e74c3c;
  color: #ffffff;
  padding: 15px 35px;
  border-radius: 5px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1em;
  transition: background-color 0.3s ease, transform 0.3s ease;
}

.cta-button:hover {
  background-color: #c0392b;
  transform: translateY(-3px);
}

.split-item.reverse {
  flex-direction: column;
}

.split-item.reverse .split-text {
  order: 1;
}

.split-item.reverse .split-image {
  order: 2;
}

@media (min-width: 992px) {
  .split-item {
    flex-direction: row;
    margin-bottom: 80px;
  }
  .split-item.reverse {
    flex-direction: row-reverse;
  }
  .split-image {
    width: 50%;
    height: 500px;
  }
  .split-text {
    width: 50%;
    padding: 60px 50px;
  }
  .split-layout-products h2 {
    font-size: 3em;
  }
}

js:
document.addEventListener('DOMContentLoaded', function() {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    images.forEach(img => {
        fetch(img.src)
            .then(response => response.json())
            .then(data => {
                if (data.hits && data.hits.length > 0) {
                    img.src = data.hits[0].largeImageURL;
                } else {
                    img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
                }
            })
            .catch(error => {
                console.error('Error fetching Pixabay image:', error);
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            });
    });
});
