css:
.team-alternating-cards-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-alternating-cards-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    gap: 4rem;
}
.team-alternating-cards-section .team-member-row {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 768px) {
    .team-alternating-cards-section .team-member-row {
        grid-template-columns: 1fr 1fr;
    }
    .team-alternating-cards-section .team-member-row:nth-child(even) .image-content {
        order: -1;
    }
}
.team-alternating-cards-section .text-content h3 {
    font-size: 1.75rem;
    font-weight: 600;
    color: #8B572A; /* Brown color like the image */
    margin-bottom: 1rem;
}
.team-alternating-cards-section .text-content p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-alternating-cards-section .image-content img {
    width: 100%;
    height: auto;
    aspect-ratio: 1/1;
    object-fit: cover;
    border-radius: 0.5rem;
}
