css:
.team-alternating-section { padding: 5rem 0; background-color: var(--color-light, #FFFFFF); }
.team-alternating-section .container { width: 90%; max-width: 900px; margin: 0 auto; }
.team-alternating-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 4rem; color: var(--color-dark, #111827); }
.team-member-row {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
    margin-bottom: 4rem;
}
@media (min-width: 768px) {
    .team-member-row { grid-template-columns: 1fr 2fr; gap: 3rem; }
    .team-member-row.reversed .member-image { order: 2; }
    .team-member-row.reversed .member-info { order: 1; text-align: right; }
}
.team-member-row .member-image img {
    width: 100%;
    aspect-ratio: 1/1;
    object-fit: cover;
    border-radius: 50%;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.member-info h3 {
    font-size: 1.75rem;
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-bottom: 0.25rem;
}
.member-title {
    display: block;
    color: var(--color-primary, #667eea);
    font-weight: 600;
    margin-bottom: 1rem;
}
.member-info p {
    color: var(--color-secondary, #6B7280);
}
