css:
.team-overlay-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-overlay-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-overlay-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 3rem;
    color: var(--color-dark, #111827);
}
.team-overlay-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
}
.team-overlay-card {
    position: relative;
    aspect-ratio: 3 / 4;
    border-radius: 1rem;
    overflow: hidden;
    color: var(--color-light, #FFFFFF);
}
.team-overlay-card .card-bg-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.4s ease;
}
.team-overlay-card:hover .card-bg-image {
    transform: scale(1.05);
}
.team-overlay-card .card-overlay {
    position: absolute;
    inset: 0;
    background: linear-gradient(to top, rgba(0,0,0,0.8) 0%, transparent 60%);
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: 1.5rem;
}
.team-overlay-card .card-content {
    transform: translateY(20px);
    transition: transform 0.4s ease;
}
.team-overlay-card:hover .card-content {
    transform: translateY(0);
}
.team-overlay-card h3 {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}
.team-overlay-card .social-icons {
    display: flex;
    gap: 1rem;
}
.team-overlay-card .social-icons a {
    color: var(--color-light, #FFFFFF);
    font-size: 1rem;
    opacity: 0.8;
    transition: opacity 0.3s, transform 0.3s;
}
.team-overlay-card .social-icons a:hover {
    opacity: 1;
    transform: scale(1.2);
}
