css:
.team-circle-section { padding: 5rem 0; background-color: #f9fafb; }
.team-circle-section .container { width: 90%; max-width: 1000px; margin: 0 auto; text-align: center; }
.team-circle-section .section-title { font-size: 2.5rem; color: var(--color-dark, #111827); }
.team-circle-section .section-subtitle { color: var(--color-secondary, #6B7280); margin-bottom: 3rem; }
.team-grid-circle {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 3rem;
}
.team-member-circle .portrait-wrapper {
    width: 150px;
    height: 150px;
    border-radius: 50%;
    overflow: hidden;
    margin: 0 auto 1.5rem;
    border: 4px solid var(--color-light, #FFFFFF);
    box-shadow: 0 5px 20px rgba(0,0,0,0.1);
    position: relative;
}
.team-member-circle .portrait-wrapper::after {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: 50%;
    border: 2px solid var(--color-primary, #667eea);
    opacity: 0;
    transform: scale(1.1);
    transition: all 0.3s ease;
}
.team-member-circle:hover .portrait-wrapper::after {
    opacity: 1;
    transform: scale(1);
}
.team-member-circle img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.team-member-circle h3 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #111827);
    margin-bottom: 0.25rem;
}
.team-member-circle span {
    color: var(--color-secondary, #6B7280);
    font-size: 0.9rem;
}
