css:
.team-bio-section { padding: 5rem 0; background-color: var(--color-light, #FFFFFF); }
.team-bio-section .container { width: 90%; max-width: 1000px; margin: 0 auto; }
.team-bio-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.team-bio-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
}
@media (min-width: 768px) { .team-bio-grid { grid-template-columns: 1fr 1fr; } }
.bio-card {
    background-color: #f9fafb;
    border-radius: 1rem;
    overflow: hidden;
    box-shadow: 0 4px 6px rgba(0,0,0,0.05);
    transition: transform 0.3s, box-shadow 0.3s;
}
.bio-card:hover { transform: translateY(-5px); box-shadow: 0 10px 20px rgba(0,0,0,0.1); }
.bio-card img {
    width: 100%;
    height: 300px;
    object-fit: cover;
}
.bio-content {
    padding: 2rem;
}
.bio-content h3 { font-size: 1.5rem; color: var(--color-dark, #111827); margin-bottom: 0.25rem; }
.bio-title {
    display: block;
    color: var(--color-primary, #667eea);
    font-weight: 500;
    margin-bottom: 1rem;
}
.bio-content p { color: var(--color-secondary, #6B7280); font-size: 0.95rem; margin-bottom: 1.5rem; }
.bio-socials {
    display: flex;
    gap: 0.75rem;
}
.bio-socials a {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    background-color: #e5e7eb;
    color: var(--color-secondary, #6B7280);
    display: flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
    font-weight: bold;
    transition: background-color 0.3s, color 0.3s;
}
.bio-socials a:hover {
    background-color: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
}
