css:
.team-grid-cta-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-grid-cta-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-grid-cta-section .grid-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2.5rem;
}
.team-grid-cta-section .cta-content {
    grid-column: 1 / -1;
}
@media (min-width: 992px) {
    .team-grid-cta-section .cta-content {
        grid-column: 1 / 2;
        grid-row: 1 / 3;
    }
    .team-grid-cta-section .grid-container {
        grid-template-columns: repeat(4, 1fr);
    }
}
@media (min-width: 768px) and (max-width: 991px) {
    .team-grid-cta-section .grid-container {
        grid-template-columns: repeat(3, 1fr);
    }
}
.team-grid-cta-section .cta-content h2 {
    font-size: 3rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.team-grid-cta-section .cta-content p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 2rem;
    line-height: 1.7;
}
.team-grid-cta-section .btn-primary {
    display: inline-block;
    padding: 0.8rem 2rem;
    background-color: var(--color-dark, #111827);
    color: var(--color-light, #FFFFFF);
    text-decoration: none;
    border-radius: 8px;
    font-weight: 600;
}
.team-grid-cta-section .team-member {
    text-align: left;
}
.team-grid-cta-section .team-member img {
    width: 100%;
    height: auto;
    aspect-ratio: 1/1;
    object-fit: cover;
    border-radius: 1rem;
    margin-bottom: 1rem;
}
.team-grid-cta-section .team-member h3 {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.team-grid-cta-section .team-member p {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
}
