css:
.team-overlay-badge-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-overlay-badge-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-overlay-badge-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-overlay-badge-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-overlay-badge-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-overlay-badge-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}
.team-overlay-badge-section .team-card {
    position: relative;
    border-radius: 1.5rem;
    overflow: hidden;
    aspect-ratio: 3/4;
    color: var(--color-light, #FFFFFF);
}
.team-overlay-badge-section .team-card img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.team-overlay-badge-section .card-badge {
    position: absolute;
    top: 1rem;
    left: 50%;
    transform: translateX(-50%);
    background-color: rgba(255,255,255,0.8);
    backdrop-filter: blur(5px);
    color: var(--color-dark, #1F2937);
    padding: 0.25rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 600;
}
.team-overlay-badge-section .card-overlay {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 1.5rem;
    background: linear-gradient(to top, rgba(0,0,0,0.8) 0%, transparent 100%);
    text-align: center;
}
.team-overlay-badge-section .card-overlay h3 {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}
.team-overlay-badge-section .card-overlay p {
    font-size: 0.9rem;
    opacity: 0.9;
}
