css:
.team-image-overlay-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-image-overlay-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-image-overlay-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-image-overlay-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-image-overlay-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-image-overlay-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}
.team-image-overlay-section .team-card {
    position: relative;
    border-radius: 1.5rem;
    overflow: hidden;
    aspect-ratio: 3/4;
    color: var(--color-light, #FFFFFF);
}
.team-image-overlay-section .team-card img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.4s ease;
}
.team-image-overlay-section .team-card:hover img {
    transform: scale(1.05);
}
.team-image-overlay-section .card-overlay {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 1.5rem;
    background: linear-gradient(to top, rgba(0,0,0,0.7) 0%, transparent 100%);
}
.team-image-overlay-section .card-overlay h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
}
.team-image-overlay-section .card-overlay p {
    font-size: 0.9rem;
    opacity: 0.9;
}
