css:
.team-minimal-section {
    padding: 5rem 0;
    background-color: #F5F3FF; /* A light lavender color */
}
.team-minimal-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.team-minimal-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
    gap: 2rem;
    justify-content: center;
}
.team-minimal-member {
    text-align: center;
}
.team-minimal-member img {
    width: 160px;
    height: 160px;
    border-radius: 50%;
    object-fit: cover;
    margin: 0 auto 1rem;
    box-shadow: 0 5px 20px rgba(0,0,0,0.1);
    transition: transform 0.3s ease;
}
.team-minimal-member:hover img {
    transform: scale(1.05);
}
.team-minimal-member h3 {
    font-size: 1.1rem;
    font-weight: 500;
    color: var(--color-dark, #111827);
}
