css:
.team-music-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-music-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-music-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
}
.team-music-card {
    position: relative;
    border-radius: 1rem;
    overflow: hidden;
    aspect-ratio: 3 / 4;
    color: var(--color-light, #FFFFFF);
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 2rem;
}
.team-music-card::after {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(0,0,0,0.6);
    z-index: 1;
}
.team-music-card .card-bg {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 0;
    transition: transform 0.4s ease;
}
.team-music-card:hover .card-bg {
    transform: scale(1.05);
}
.team-music-card .card-content {
    position: relative;
    z-index: 2;
}
.team-music-card .portrait {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    object-fit: cover;
    border: 3px solid var(--color-light, #FFFFFF);
    margin-bottom: 1rem;
}
.team-music-card h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}
.team-music-card p {
    font-size: 0.9rem;
    margin-bottom: 1rem;
    opacity: 0.9;
}
.team-music-card .social-icons {
    display: flex;
    justify-content: center;
    gap: 1rem;
}
.team-music-card .social-icons a {
    color: var(--color-light, #FFFFFF);
    font-size: 1.1rem;
}
