css:
.team-portraits-section { padding: 5rem 0; background-color: var(--color-light); }
.team-portraits-section .container { width: 90%; max-width: 800px; margin: 0 auto; text-align: center; }
.team-portraits-section .section-header h2 { font-size: 2.5rem; margin-bottom: 2rem; color: var(--color-dark); position: relative; display: inline-block; }
.team-portraits-section .section-header h2::before, .team-portraits-section .section-header h2::after {
    content: '';
    position: absolute;
    height: 2px;
    width: 30px;
    background-color: #ccc;
    top: 50%;
}
.team-portraits-section .section-header h2::before { right: 100%; margin-right: 1rem; }
.team-portraits-section .section-header h2::after { left: 100%; margin-left: 1rem; }
.team-portraits-section .portraits-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; margin-bottom: 2rem; padding-bottom: 1rem; border-bottom: 1px solid #ccc; }
.team-portraits-section .portrait-item img { width: 100%; object-fit: cover; aspect-ratio: 3/4; filter: grayscale(100%) contrast(1.1); }
.team-portraits-section .team-description { padding: 2rem; margin-bottom: 2rem; }
.team-portraits-section .team-description p { color: var(--color-secondary); margin: 0; }
.team-portraits-section .btn-dark { background-color: var(--color-dark); color: var(--color-light); padding: 0.75rem 2.5rem; text-decoration: none; font-weight: 600; transition: all 0.3s; }
.team-portraits-section .btn-dark:hover { background-color: #333; }
.team-portraits-section .anim-delay-1 { transition-delay: 0.1s; }
.team-portraits-section .anim-delay-2 { transition-delay: 0.2s; }
.team-portraits-section .anim-delay-3 { transition-delay: 0.3s; }
