css:
.team-simple-image-grid-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-simple-image-grid-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-simple-image-grid-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-simple-image-grid-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-simple-image-grid-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-simple-image-grid-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1.5rem;
}
.team-simple-image-grid-section .team-member img {
    width: 100%;
    height: auto;
    aspect-ratio: 1/1;
    object-fit: cover;
    border-radius: 1.5rem;
}
