css:
.team-small-cards-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-small-cards-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-small-cards-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-small-cards-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-small-cards-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-small-cards-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
    justify-items: center;
}
.team-small-cards-section .team-member {
    text-align: center;
}
.team-small-cards-section .team-member img {
    width: 120px;
    height: 120px;
    border-radius: 1.5rem;
    object-fit: cover;
    margin: 0 auto 1rem;
}
.team-small-cards-section .team-member h3 {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    margin-bottom: 0.25rem;
}
.team-small-cards-section .team-member p {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 0.75rem;
}
.team-small-cards-section .social-icons {
    display: flex;
    justify-content: center;
    gap: 0.75rem;
}
.team-small-cards-section .social-icons a {
    color: var(--color-secondary, #6B7280);
    font-size: 1rem;
    transition: color 0.3s;
}
.team-small-cards-section .social-icons a:hover {
    color: var(--color-dark, #1F2937);
}
