css:
.team-split-bio-checklist-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    font-family: 'Inter', sans-serif;
}
.team-split-bio-checklist-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-split-bio-checklist-section .split-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 992px) {
    .team-split-bio-checklist-section .split-grid {
        grid-template-columns: 45% 55%;
        gap: 3rem;
    }
}
.team-split-bio-checklist-section .image-wrapper {
    position: relative;
    border-radius: 1rem;
    overflow: hidden;
    width: 100%;
    aspect-ratio: 1 / 1;
}
.team-split-bio-checklist-section .image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.team-split-bio-checklist-section .image-badge {
    position: absolute;
    top: 1.5rem;
    left: 1.5rem;
    background-color: rgba(0,0,0,0.6);
    color: var(--color-light, #FFFFFF);
    padding: 0.5rem 1rem;
    border-radius: 0.5rem;
    font-size: 0.8rem;
    font-weight: 600;
}
.team-split-bio-checklist-section .content-wrapper {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1rem;
    padding: 3rem;
    color: var(--color-dark, #1F2937);
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.team-split-bio-checklist-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 1rem;
}
.team-split-bio-checklist-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin-bottom: 1rem;
}
.team-split-bio-checklist-section .divider {
    border: 0;
    height: 1px;
    background-color: #d1d5db; /* gray-300 */
    margin: 2rem 0;
}
.team-split-bio-checklist-section .checklist {
    list-style: none;
    padding: 0;
    margin-top: 2rem;
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.team-split-bio-checklist-section .checklist li {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    font-weight: 500;
}
.team-split-bio-checklist-section .checklist i {
    color: var(--color-dark, #1F2937);
}
