css:
.team-split-bio-open-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    font-family: 'Inter', sans-serif;
}
.team-split-bio-open-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.team-split-bio-open-section .split-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 768px) {
    .team-split-bio-open-section .split-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.team-split-bio-open-section .image-wrapper img {
    width: 100%;
    height: auto;
    border-radius: 1rem;
    aspect-ratio: 1 / 1;
    object-fit: cover;
}
.team-split-bio-open-section .content-wrapper {
    text-align: left;
}
.team-split-bio-open-section .subtitle {
    color: var(--color-secondary, #6B7280);
    font-weight: 500;
}
.team-split-bio-open-section h2 {
    font-size: 3rem;
    font-weight: 800;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.team-split-bio-open-section p:not(.subtitle) {
    color: var(--color-dark, #1F2937);
    opacity: 0.8;
    margin-bottom: 1.5rem;
    max-width: 450px;
}
.team-split-bio-open-section .signature {
    width: 150px;
    margin-top: 2rem;
}
