css:
.team-split-bio-signature-section {
    padding: 5rem 0;
    background-color: #f3f4f6; /* gray-100 */
    font-family: 'Inter', sans-serif;
}
.team-split-bio-signature-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.team-split-bio-signature-section .split-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.5rem;
    align-items: stretch; /* Make cards same height */
}
@media (min-width: 992px) {
    .team-split-bio-signature-section .split-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.team-split-bio-signature-section .content-wrapper,
.team-split-bio-signature-section .image-wrapper {
    background-color: var(--color-light, #FFFFFF);
    border-radius: 1rem;
    padding: 3rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.team-split-bio-signature-section .image-wrapper {
    padding: 0;
    overflow: hidden;
}
.team-split-bio-signature-section .image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    aspect-ratio: 1 / 1;
}
.team-split-bio-signature-section h2 {
    font-size: 3rem;
    font-weight: 800;
    color: var(--color-dark, #1F2937);
    margin-bottom: 0.5rem;
}
.team-split-bio-signature-section .subtitle {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 2rem;
    font-weight: 500;
}
.team-split-bio-signature-section p:not(.subtitle) {
    color: var(--color-dark, #1F2937);
    opacity: 0.8;
    margin-bottom: 1.5rem;
}
.team-split-bio-signature-section .signature {
    width: 150px;
    margin-top: 2rem;
}
