css:
.team-split-overlap-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-split-overlap-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .team-split-overlap-section .container {
        grid-template-columns: 40% 60%;
    }
}
.team-split-overlap-section .content-side h2 {
    font-size: 3rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.team-split-overlap-section .content-side p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 2rem;
    line-height: 1.7;
}
.team-split-overlap-section .btn-primary {
    display: inline-block;
    padding: 0.8rem 2rem;
    background-color: var(--color-dark, #111827);
    color: var(--color-light, #FFFFFF);
    text-decoration: none;
    border-radius: 8px;
    font-weight: 600;
}
.team-split-overlap-section .images-side {
    position: relative;
    height: 450px;
}
.team-split-overlap-section .team-image {
    position: absolute;
    border-radius: 1.5rem;
    object-fit: cover;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.team-split-overlap-section .image-1 {
    width: 60%;
    height: 70%;
    top: 0;
    left: 0;
}
.team-split-overlap-section .image-2 {
    width: 50%;
    height: 60%;
    top: 40%;
    left: 50%;
    z-index: 2;
}
.team-split-overlap-section .image-3 {
    width: 40%;
    height: 50%;
    top: 10%;
    left: 60%;
    z-index: 1;
}
.team-split-overlap-section .image-text {
    position: absolute;
    background: rgba(255,255,255,0.8);
    backdrop-filter: blur(5px);
    padding: 0.5rem 1rem;
    border-radius: 0.75rem;
    z-index: 3;
    text-align: center;
}
.team-split-overlap-section .image-text-1 { bottom: 35%; left: 5%; }
.team-split-overlap-section .image-text-2 { bottom: 5%; left: 45%; }
.team-split-overlap-section .image-text-3 { top: 5%; right: 0; }
.team-split-overlap-section .image-text h3 { font-size: 1rem; font-weight: 600; }
.team-split-overlap-section .image-text p { font-size: 0.8rem; color: var(--color-secondary, #6B7280); margin: 0; }
