css:
.team-circular-bio-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-circular-bio-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-circular-bio-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-circular-bio-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-circular-bio-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-circular-bio-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 2rem;
}
.team-circular-bio-section .team-member {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1.5rem;
    padding: 2rem;
    display: flex;
    align-items: center;
    gap: 1.5rem;
}
.team-circular-bio-section .team-member img {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    object-fit: cover;
    flex-shrink: 0;
}
.team-circular-bio-section .member-info h3 {
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.team-circular-bio-section .member-info .role {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 0.75rem;
}
.team-circular-bio-section .member-info p:not(.role) {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin-bottom: 1rem;
}
.team-circular-bio-section .social-icons {
    display: flex;
    gap: 0.75rem;
}
.team-circular-bio-section .social-icons a {
    color: var(--color-secondary, #6B7280);
    font-size: 1rem;
}
