css:
.team-two-row-grid-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-two-row-grid-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-two-row-grid-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-two-row-grid-section .section-header h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-two-row-grid-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-two-row-grid-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
}
.team-two-row-grid-section .team-member {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1.5rem;
    padding: 2rem;
}
.team-two-row-grid-section .team-member img {
    width: 100%;
    height: auto;
    aspect-ratio: 1/1;
    object-fit: cover;
    border-radius: 1rem;
    margin-bottom: 1.5rem;
}
.team-two-row-grid-section .team-member h3 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.team-two-row-grid-section .team-member .role {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 1rem;
}
.team-two-row-grid-section .team-member p:not(.role) {
    font-size: 0.95rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
}
