css:
.team-vertical-bio-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-vertical-bio-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.team-vertical-bio-section .section-header {
    text-align: center;
    max-width: 700px;
    margin: 0 auto 4rem;
}
.team-vertical-bio-section h2 {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.team-vertical-bio-section .section-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.team-vertical-bio-section .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
}
.team-vertical-bio-section .team-card {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1.5rem;
    overflow: hidden;
}
.team-vertical-bio-section .team-card img {
    width: 100%;
    height: auto;
    aspect-ratio: 1/1;
    object-fit: cover;
}
.team-vertical-bio-section .card-content {
    padding: 2rem;
}
.team-vertical-bio-section .card-content h3 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.team-vertical-bio-section .card-content .role {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 1rem;
}
.team-vertical-bio-section .card-content .bio {
    font-size: 0.95rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin-bottom: 1.5rem;
}
.team-vertical-bio-section .social-icons {
    display: flex;
    gap: 1rem;
}
.team-vertical-bio-section .social-icons a {
    color: var(--color-secondary, #6B7280);
    font-size: 1.2rem;
}
