css:
.team-vertical-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.team-vertical-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
}
.team-list {
    display: flex;
    flex-direction: column;
    gap: 4rem;
}
.team-list-item {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 768px) {
    .team-list-item {
        grid-template-columns: 200px 1fr;
    }
}
.team-list-item .item-image img {
    width: 100%;
    height: auto;
    aspect-ratio: 1 / 1;
    object-fit: cover;
    border-radius: 0.5rem;
}
.team-list-item .item-content h3 {
    font-size: 1.75rem;
    font-weight: 600;
    color: var(--color-dark, #111827);
    margin-bottom: 0.75rem;
}
.team-list-item .item-content p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
