css:
.section-hero-modern-tabs-a1b2 {
  --color-primary: #4A90E2;
  --color-secondary: #50E3C2;
  --color-dark: #333333;
  --color-light: #FFFFFF;
  --color-surface-1: #F8F9FA;
  --color-border: #E0E0E0;
  --font-heading: 'Poppins', sans-serif;
  --font-body: 'Open Sans', sans-serif;
  font-family: var(--font-body);
  padding: 80px 0;
  background-color: var(--color-light);
  color: var(--color-dark);
}

.section-hero-modern-tabs-a1b2 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.section-hero-modern-tabs-a1b2 .hero-content {
  margin-bottom: 60px;
  max-width: 800px;
}

.section-hero-modern-tabs-a1b2 .hero-title {
  font-family: var(--font-heading);
  font-size: clamp(2.5rem, 6vw, 3.5rem);
  margin-bottom: 20px;
  line-height: 1.2;
  color: var(--color-dark);
}

.section-hero-modern-tabs-a1b2 .hero-subtitle {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: var(--color-secondary);
  line-height: 1.6;
}

.section-hero-modern-tabs-a1b2 .hero-cta {
  background-color: var(--color-primary);
  color: var(--color-light);
  padding: 15px 30px;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.section-hero-modern-tabs-a1b2 .hero-cta:hover {
  background-color: #357ABD;
}

.section-hero-modern-tabs-a1b2 .testimonial-tabs {
  width: 100%;
  max-width: 800px;
  margin-top: 40px;
}

.section-hero-modern-tabs-a1b2 .tab-buttons {
  display: flex;
  justify-content: center;
  gap: 15px;
  margin-bottom: 30px;
  flex-wrap: wrap;
}

.section-hero-modern-tabs-a1b2 .tab-button {
  background-color: transparent;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  font-weight: bold;
  color: var(--color-dark);
  cursor: pointer;
  transition: color 0.3s ease, border-bottom 0.3s ease;
  border-bottom: 2px solid transparent;
}

.section-hero-modern-tabs-a1b2 .tab-button.active {
  color: var(--color-primary);
  border-bottom: 2px solid var(--color-primary);
}

.section-hero-modern-tabs-a1b2 .tab-content {
  position: relative;
  min-height: 250px;
}

.section-hero-modern-tabs-a1b2 .tab-pane {
  display: none;
  text-align: center;
  padding: 20px;
  background-color: var(--color-surface-1);
  border-radius: 8px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
}

.section-hero-modern-tabs-a1b2 .tab-pane.active {
  display: block;
}

.section-hero-modern-tabs-a1b2 .testimonial-card {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.section-hero-modern-tabs-a1b2 .testimonial-avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  margin-bottom: 20px;
  object-fit: cover;
  border: 3px solid var(--color-primary);
}

.section-hero-modern-tabs-a1b2 .testimonial-quote {
  font-size: 1.1rem;
  line-height: 1.7;
  margin-bottom: 20px;
  color: var(--color-dark);
  font-style: italic;
}

.section-hero-modern-tabs-a1b2 .testimonial-author {
  display: flex;
  flex-direction: column;
}

.section-hero-modern-tabs-a1b2 .author-name {
  font-weight: bold;
  color: var(--color-dark);
}

.section-hero-modern-tabs-a1b2 .author-title {
  font-size: 0.9rem;
  color: var(--color-secondary);
}

@media (min-width: 768px) {
  .section-hero-modern-tabs-a1b2 .container {
    flex-direction: row;
    text-align: left;
    justify-content: space-between;
    gap: 40px;
  }
  .section-hero-modern-tabs-a1b2 .hero-content {
    flex: 1;
    margin-bottom: 0;
    max-width: 500px;
  }
  .section-hero-modern-tabs-a1b2 .testimonial-tabs {
    flex: 1;
    margin-top: 0;
  }
  .section-hero-modern-tabs-a1b2 .tab-buttons {
    justify-content: flex-start;
  }
}

@media (min-width: 1024px) {
  .section-hero-modern-tabs-a1b2 {
    padding: 100px 0;
  }
  .section-hero-modern-tabs-a1b2 .hero-title {
    font-size: 3.8rem;
  }
  .section-hero-modern-tabs-a1b2 .hero-subtitle {
    font-size: 1.2rem;
  }
}
