css:
.section-hero-modern-tabs-a7 {
  background-color: var(--color-light);
}

.section-hero-modern-tabs-a7 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 60px 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.section-hero-modern-tabs-a7 .hero-content {
  margin-bottom: 40px;
  max-width: 700px;
}

.section-hero-modern-tabs-a7 .hero-title {
  font-size: clamp(2.5rem, 6vw, 4rem);
  font-family: var(--font-heading);
  color: var(--color-dark);
  margin-bottom: 20px;
}

.section-hero-modern-tabs-a7 .hero-subtitle {
  font-size: 1.1rem;
  color: var(--color-secondary);
  font-family: var(--font-body);
  margin-bottom: 30px;
  line-height: 1.6;
}

.section-hero-modern-tabs-a7 .cta-button {
  padding: 12px 30px;
  font-size: 1rem;
  font-weight: bold;
  font-family: var(--font-body);
  color: var(--color-light);
  background-color: var(--color-primary);
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.section-hero-modern-tabs-a7 .cta-button:hover {
  background-color: var(--color-primary-darker, #0056b3);
}

.section-hero-modern-tabs-a7 .tabs-container {
  width: 100%;
  margin-top: 40px;
}

.section-hero-modern-tabs-a7 .tab-headers {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-bottom: 30px;
  flex-wrap: wrap;
}

.section-hero-modern-tabs-a7 .tab-header {
  padding: 10px 20px;
  font-size: 1rem;
  font-family: var(--font-body);
  color: var(--color-secondary);
  background-color: transparent;
  border: 1px solid var(--color-border);
  border-radius: 5px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.section-hero-modern-tabs-a7 .tab-header.active {
  color: var(--color-primary);
  border-color: var(--color-primary);
  font-weight: bold;
}

.section-hero-modern-tabs-a7 .tab-header:not(.active):hover {
  background-color: var(--color-surface-1);
  color: var(--color-dark);
}

.section-hero-modern-tabs-a7 .tab-content {
  position: relative;
  min-height: 250px;
}

.section-hero-modern-tabs-a7 .tab-pane {
  display: none;
  animation: fadeIn 0.5s ease-in-out;
}

.section-hero-modern-tabs-a7 .tab-pane.active {
  display: block;
}

.section-hero-modern-tabs-a7 .testimonial-card {
  background-color: var(--color-surface-1);
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  max-width: 500px;
  margin: 0 auto;
}

.section-hero-modern-tabs-a7 .testimonial-avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid var(--color-primary);
}

.section-hero-modern-tabs-a7 .testimonial-quote {
  font-size: 1.1rem;
  font-family: var(--font-body);
  color: var(--color-dark);
  line-height: 1.7;
  font-style: italic;
  margin: 0;
}

.section-hero-modern-tabs-a7 .testimonial-author {
  font-size: 0.95rem;
  font-family: var(--font-body);
  color: var(--color-secondary);
  margin-top: 15px;
  font-weight: bold;
  font-style: normal;
}

@media (min-width: 768px) {
  .section-hero-modern-tabs-a7 .hero-content {
    padding: 0 20px;
  }
  .section-hero-modern-tabs-a7 .testimonial-card {
    flex-direction: row;
    text-align: left;
    max-width: 700px;
  }
  .section-hero-modern-tabs-a7 .testimonial-avatar {
    margin-bottom: 0;
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}
