css:
.section-hero-modern-tabs-ak9z {
  padding: 60px 0;
  background-color: var(--color-light);
  color: var(--color-dark);
  font-family: var(--font-body);
}

.section-hero-modern-tabs-ak9z .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
}

.section-hero-modern-tabs-ak9z .hero-content {
  text-align: center;
}

.section-hero-modern-tabs-ak9z .hero-title {
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-family: var(--font-heading);
  margin-bottom: 15px;
  color: var(--color-dark);
}

.section-hero-modern-tabs-ak9z .hero-subtitle {
  font-size: 1.1rem;
  color: var(--color-secondary);
  margin-bottom: 40px;
  line-height: 1.6;
}

.section-hero-modern-tabs-ak9z .tab-container {
  margin-top: 30px;
}

.section-hero-modern-tabs-ak9z .tab-buttons {
  display: flex;
  justify-content: center;
  gap: 15px;
  margin-bottom: 30px;
  flex-wrap: wrap;
}

.section-hero-modern-tabs-ak9z .tab-button {
  padding: 10px 20px;
  border: 1px solid var(--color-border);
  background-color: transparent;
  cursor: pointer;
  font-family: var(--font-heading);
  font-weight: 500;
  transition: all 0.3s ease;
  border-radius: 5px;
  color: var(--color-dark);
}

.section-hero-modern-tabs-ak9z .tab-button:hover {
  background-color: var(--color-primary);
  color: var(--color-light);
  border-color: var(--color-primary);
}

.section-hero-modern-tabs-ak9z .tab-button.active {
  background-color: var(--color-primary);
  color: var(--color-light);
  border-color: var(--color-primary);
}

.section-hero-modern-tabs-ak9z .tab-panels {
  position: relative;
  min-height: 300px;
}

.section-hero-modern-tabs-ak9z .tab-panel {
  display: none;
  animation: fadeIn 0.5s ease;
}

.section-hero-modern-tabs-ak9z .tab-panel.active {
  display: block;
}

.section-hero-modern-tabs-ak9z .testimonial-card {
  background-color: var(--color-surface-1);
  border-radius: 8px;
  padding: 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
  max-width: 600px;
  margin: 0 auto;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

@media (min-width: 768px) {
  .section-hero-modern-tabs-ak9z .testimonial-card {
    flex-direction: row;
    text-align: left;
  }
  .section-hero-modern-tabs-ak9z .tab-container {
    text-align: left;
  }
}

.section-hero-modern-tabs-ak9z .testimonial-img {
  width: 100px;
  height: 100px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid var(--color-primary);
}

.section-hero-modern-tabs-ak9z .testimonial-text {
  flex: 1;
}

.section-hero-modern-tabs-ak9z .testimonial-quote {
  font-size: 1.1rem;
  line-height: 1.7;
  margin-bottom: 15px;
  font-style: italic;
  color: var(--color-dark);
}

.section-hero-modern-tabs-ak9z .testimonial-author {
  font-weight: bold;
  color: var(--color-primary);
  font-family: var(--font-heading);
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
