css:
.section-hero-tabs-interactive-a1b2 .container { max-width: 1200px; margin: 0 auto; padding: 40px 20px; }
.section-hero-tabs-interactive-a1b2 .hero-content { text-align: center; }
.section-hero-tabs-interactive-a1b2 .hero-title { font-size: clamp(2.5rem, 5vw, 3.5rem); color: var(--color-dark); margin-bottom: 16px; font-family: var(--font-heading); }
.section-hero-tabs-interactive-a1b2 .hero-subtitle { font-size: 1.1rem; color: var(--color-secondary); margin-bottom: 40px; font-family: var(--font-body); }
.section-hero-tabs-interactive-a1b2 .tabs-container { }
.section-hero-tabs-interactive-a1b2 .tab-buttons { display: flex; justify-content: center; gap: 15px; margin-bottom: 30px; flex-wrap: wrap; }
.section-hero-tabs-interactive-a1b2 .tab-button { padding: 10px 20px; font-size: 1rem; font-family: var(--font-body); background-color: transparent; border: none; border-bottom: 2px solid transparent; cursor: pointer; color: var(--color-secondary); transition: color 0.3s ease, border-color 0.3s ease; }
.section-hero-tabs-interactive-a1b2 .tab-button.active { color: var(--color-primary); border-bottom-color: var(--color-primary); font-weight: bold; }
.section-hero-tabs-interactive-a1b2 .tab-button:hover:not(.active) { color: var(--color-dark); }
.section-hero-tabs-interactive-a1b2 .tab-panels { }
.section-hero-tabs-interactive-a1b2 .tab-panel { display: none; text-align: center; }
.section-hero-tabs-interactive-a1b2 .tab-panel.active { display: block; animation: fadeIn 0.5s ease-in-out; }
.section-hero-tabs-interactive-a1b2 .testimonial-card { background-color: var(--color-surface-1); padding: 30px; border-radius: 8px; box-shadow: 0 4px 15px rgba(0,0,0,0.1); max-width: 600px; margin: 0 auto; }
.section-hero-tabs-interactive-a1b2 .testimonial-avatar { width: 80px; height: 80px; border-radius: 50%; margin-bottom: 20px; object-fit: cover; border: 3px solid var(--color-primary); }
.section-hero-tabs-interactive-a1b2 .testimonial-text { font-size: 1.1rem; color: var(--color-dark); margin-bottom: 20px; font-family: var(--font-body); line-height: 1.6; }
.section-hero-tabs-interactive-a1b2 .testimonial-author { }
.section-hero-tabs-interactive-a1b2 .author-name { display: block; font-weight: bold; color: var(--color-dark); font-family: var(--font-heading); }
.section-hero-tabs-interactive-a1b2 .author-title { display: block; font-size: 0.9rem; color: var(--color-secondary); font-family: var(--font-body); }

@keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
