css:
.before-after-section { padding: 4rem 0; background: #f3f4f6; position: relative; width: 100%; box-sizing: border-box; }
.before-after-section * { box-sizing: border-box; }
.before-after-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.before-after-section .story-card { background: #fff; border-radius: 1rem; box-shadow: 0 10px 20px rgba(0,0,0,0.05); overflow: hidden; }
.before-after-section .split-column { padding: 2rem; }
.before-after-section .split-column h3 { font-size: 1rem; text-transform: uppercase; font-weight: 700; margin-bottom: 0.5rem; }
.before-after-section .before { background: #fee2e2; }
.before-after-section .before h3 { color: #b91c1c; }
.before-after-section .after { background: #dcfce7; }
.before-after-section .after h3 { color: #166534; }
.before-after-section .split-column p { font-style: italic; color: #4b5563; margin: 0; }
.before-after-section .author-quote { padding: 2rem; border-top: 1px solid #e5e7eb; background-color: #fff; }
.before-after-section .author-quote p { font-weight: 500; margin-bottom: 0.5rem; color: var(--color-dark, #111827); }
.before-after-section .author-quote cite { font-style: normal; font-size: 0.9rem; color: var(--color-secondary, #6b7280); }
@media(min-width: 640px) { .before-after-section .story-card { display: grid; grid-template-columns: 1fr 1fr; grid-template-rows: auto auto; } .before-after-section .author-quote { grid-column: 1 / -1; } }
