css:
.chat-thread-section { padding: 4rem 0; background-color: #e5e7eb; position: relative; width: 100%; box-sizing: border-box; }
.chat-thread-section * { box-sizing: border-box; }
.chat-thread-section .container { max-width: 700px; margin: 0 auto; padding: 0 1rem; display: flex; flex-direction: column; gap: 1.5rem; }
.chat-thread-section .chat-message { display: flex; gap: 0.75rem; max-width: 80%; align-items: flex-end; }
.chat-thread-section .chat-message.sent { align-self: flex-end; justify-content: flex-end; }
.chat-thread-section .chat-message img { width: 40px; height: 40px; border-radius: 50%; flex-shrink: 0; }
.chat-thread-section .chat-bubble { padding: 1rem; border-radius: 1.25rem; }
.chat-thread-section .received .chat-bubble { background: #fff; border-bottom-left-radius: 0.25rem; color: #333; }
.chat-thread-section .sent .chat-bubble { background: #3b82f6; color: #fff; border-bottom-right-radius: 0.25rem; }
.chat-thread-section .chat-bubble p { margin: 0; font-size: 0.95rem; line-height: 1.5; }
